public class MessageMonitor {
    private Jedis jedis;
    private long threshold = 60000; // 1

    public MessageMonitor(String host, int port) {
        this.jedis = new Jedis(host, port);
    }

    public void checkMessages() {
        Set<String> messages = jedis.hkeys("hash:");
        for (String message : messages) {
            long startTime = Long.parseLong(jedis.hget("hash:", message));
            if (new Date().getTime() - startTime > threshold) {
                jedis.lpush("queue:", message);
                jedis.hdel("hash:", message);
            }
        }
    }

    public void scheduleCheck() {
        new ScheduledThreadPoolExecutor(1).scheduleAtFixedRate(() -> checkMessages(), 0, 30, TimeUnit.SECONDS);
    }

    public void close() {
        jedis.close();
    }
}
