import redis.clients.jedis.Jedis;
import redis.clients.jedis.Scripting;

public class RedisConsumer {
    private Jedis jedis;
    private String moveScript = "local message = redis.call('RPOP', KEYS[1]); if message then redis.call('LPUSH', KEYS[2], message); end; return message;";

    public RedisConsumer(String host, int port) {
        this.jedis = new Jedis(host, port);
    }

    public String consume(StringQueue, StringQueue) {
        String message = (String) jedis.eval(moveScript, 2,Queue,Queue);
        if (message != null) {
            jedis.hset("hash:", message, String.valueOf(System.currentTimeMillis()));
        }
        return message;
    }

    public void confirm(StringQueue, String message) {
        jedis.lrem(Queue, 0, message);
        jedis.hdel("hash:", message);
    }

    public void close() {
        jedis.close();
    }
}
